mapper.select:
	  select ID as id, NAME as name, FUNC_TYPE as funcType, FUNC_URL as funcUrl, PID as pid, FUNC_SEQ as funcSeq, SITE_ID as siteId from SYS_FUNC where 1=1 

        and ID = #{id,jdbcType=VARCHAR}


        and NAME like CONCAT('%',#{name,jdbcType=VARCHAR},'%')


        and FUNC_TYPE = #{funcType,jdbcType=VARCHAR}


        and FUNC_URL = #{funcUrl,jdbcType=VARCHAR}


        and PID = #{pid,jdbcType=VARCHAR}


        and FUNC_SEQ = #{funcSeq,jdbcType=DECIMAL}


        and SITE_ID = #{siteId,jdbcType=DECIMAL}


       	limit #{beginNum},#{onePageSize}

	  select count(*) from SYS_FUNC where 1=1 

        and ID = #{id,jdbcType=VARCHAR}


        and NAME like CONCAT('%',#{name,jdbcType=VARCHAR},'%')


        and FUNC_TYPE = #{funcType,jdbcType=VARCHAR}


        and FUNC_URL = #{funcUrl,jdbcType=VARCHAR}


        and PID = #{pid,jdbcType=VARCHAR}


        and FUNC_SEQ = #{funcSeq,jdbcType=DECIMAL}


        and SITE_ID = #{siteId,jdbcType=DECIMAL}

mapper.delete:

DELETE FROM SYS_FUNC WHERE 1=1

  and ID = #{id,jdbcType=VARCHAR}


  and NAME = #{name,jdbcType=VARCHAR}


  and FUNC_TYPE = #{funcType,jdbcType=VARCHAR}


  and FUNC_URL = #{funcUrl,jdbcType=VARCHAR}


  and PID = #{pid,jdbcType=VARCHAR}


  and FUNC_SEQ = #{funcSeq,jdbcType=DECIMAL}


  and SITE_ID = #{siteId,jdbcType=DECIMAL}

mapper.update:
UPDATE SYS_FUNC
NAME = #{name,jdbcType=VARCHAR}, FUNC_TYPE = #{funcType,jdbcType=VARCHAR}, FUNC_URL = #{funcUrl,jdbcType=VARCHAR}, PID = #{pid,jdbcType=VARCHAR}, FUNC_SEQ = #{funcSeq,jdbcType=DECIMAL}, SITE_ID = #{siteId,jdbcType=DECIMAL},
WHERE ID=#{id,jdbcType=VARCHAR}
