mapper.select:
	  select USER_ID as userId, USER_NAME as userName, PASSWORD as password, NICK_NAME as nickName, EMAIL as email from SYS_USER where 1=1 

        and USER_ID = #{userId,jdbcType=VARCHAR}


        and USER_NAME like CONCAT('%',#{userName,jdbcType=VARCHAR},'%')


        and PASSWORD = #{password,jdbcType=VARCHAR}


        and NICK_NAME = #{nickName,jdbcType=VARCHAR}


        and EMAIL = #{email,jdbcType=VARCHAR}


       	limit #{beginNum},#{onePageSize}

	  select count(*) from SYS_USER where 1=1 

        and USER_ID = #{userId,jdbcType=VARCHAR}


        and USER_NAME like CONCAT('%',#{userName,jdbcType=VARCHAR},'%')


        and PASSWORD = #{password,jdbcType=VARCHAR}


        and NICK_NAME = #{nickName,jdbcType=VARCHAR}


        and EMAIL = #{email,jdbcType=VARCHAR}

mapper.delete:

DELETE FROM SYS_USER WHERE 1=1

  and USER_ID = #{userId,jdbcType=VARCHAR}


  and USER_NAME = #{userName,jdbcType=VARCHAR}


  and PASSWORD = #{password,jdbcType=VARCHAR}


  and NICK_NAME = #{nickName,jdbcType=VARCHAR}


  and EMAIL = #{email,jdbcType=VARCHAR}

mapper.update:
UPDATE SYS_USER
USER_NAME = #{userName,jdbcType=VARCHAR}, PASSWORD = #{password,jdbcType=VARCHAR}, NICK_NAME = #{nickName,jdbcType=VARCHAR}, EMAIL = #{email,jdbcType=VARCHAR},
WHERE USER_ID=#{userId,jdbcType=VARCHAR}
